import React, { useState } from 'react';
import classNames from 'classnames';
import { SectionProps } from '../../utils/SectionProps';
import ButtonGroup from '../elements/ButtonGroup';
import Button from '../elements/Button';
import Image from '../elements/Image';
import Modal from '../elements/Modal';
import LandingPageImage from './partials/LandingPageImage';

const propTypes = {
  ...SectionProps.types
}

const defaultProps = {
  ...SectionProps.defaults
}

const Hero = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  ...props
}) => {

  const [videoModalActive, setVideomodalactive] = useState(false);

  const openModal = (e) => {
    e.preventDefault();
    setVideomodalactive(true);
  }

  const closeModal = (e) => {
    e.preventDefault();
    setVideomodalactive(false);
  }

  const outerClasses = classNames(
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'hero-inner section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const headerLeft = {
    textAlign: 'left'
  };

  const leftBox = {
    marginTop: "60px",
    marginBottom: "140px",
  };

  const splitClasses = classNames(
    'split-wrap'
  );


  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className={splitClasses}>
        <div className="split-item">
          <div className="split-item-content center-content-mobile reveal-from-left" data-reveal-container=".split-item">
              <div className="hero-content" style={headerLeft}>
                <h2 className="mt-16 mb-32 reveal-from-bottom" data-reveal-delay="200">
                  Tier 1 Mentoring for <br></br><span className="text-color-primary">Engineers</span> and <span className="text-color-primary">Students</span>
                </h2>
                <div className="container-sm">
                  <p className="mb-32 reveal-from-bottom" data-reveal-delay="400">
                    We have engineers from the best companies to <br></br> help you find prepare for technical interview and <br></br> gain advice to improve your career
                </p>
                  <div className="reveal-from-bottom" data-reveal-delay="600">
                    <ButtonGroup>
                      <Button tag="a" color="primary" wideMobile href="https://cruip.com/">
                        Get started
                    </Button>
                      <Button tag="a" color="dark" wideMobile href="https://github.com/cruip/open-react-template/">
                        Contact Us
                    </Button>
                    </ButtonGroup>
                  </div>
                </div>
              </div>
            <div className={
              classNames(
                'split-item-image center-content-mobile reveal-from-bottom'
                && 'split-item-image-fill'
              )}
              data-reveal-container=".split-item">
              <Image
                src={require('./../../assets/images/features-split-image-03.png')}
                alt="Features split 03"
                width={528}
                height={396} />
            </div>
          </div>
        </div>
        </div>
    </section>
  );
}

Hero.propTypes = propTypes;
Hero.defaultProps = defaultProps;

export default Hero;


